schemaSpec.groovy:

// in short the oracle primary key depends on a constraint from MEASUREMENT_CLASS which was
// auto generated somehow.  Not sure exactly how this happened but in order to ensure that
// this occured on all HQ Oracle installations the hasConstraint var is used
def row = SQL.firstRow("SELECT constraint_name FROM user_constraints WHERE table_name = 'EAM_MEASUREMENT' and constraint_type = 'P' and index_name = 'MEAS_ID_CLASS'")
def stmt
def hasConstraint = (row != null && row.constraint_name != null)
if (hasConstraint) {
    // SQL.execute("ALTER ...") does not work here for some odd reason
    try {
        stmt = CONN.createStatement()
        def sql = "ALTER table EAM_MEASUREMENT DROP CONSTRAINT ${row.constraint_name} CASCADE"
        println("${sql}")
        stmt.execute(sql)
   } finally {
        stmt.close()
   }
}
SQL.execute("ALTER table EAM_MEASUREMENT DROP COLUMN MEASUREMENT_CLASS")
// primary key is deleted as a result of dropping the MEAS_ID_CLASS constraint.
// also, since cascade was used we need to regenerate the foreign key references
// that were dropped as a result
if (hasConstraint) {
    SQL.execute("ALTER table EAM_MEASUREMENT ADD PRIMARY KEY (ID)")
    SQL.execute("ALTER table EAM_METRIC_AUX_LOGS ADD CONSTRAINT FKE7D70B0BA1FCB528 foreign key (METRIC_ID) references EAM_MEASUREMENT (ID)")
    SQL.execute("ALTER table EAM_MEASUREMENT_BL ADD CONSTRAINT FK1877029BB6E3839C foreign key (MEASUREMENT_ID) references EAM_MEASUREMENT (ID) on delete cascade")
}

schema-directSQL.statement:
update EAM_RES_GRP_RES_MAP set ID = (
    select (
        select count(*) from EAM_RES_GRP_RES_MAP m
        where EAM_RES_GRP_RES_MAP.resource_group_id > m.resource_group_id
    ) + (
        select count(*) from EAM_RES_GRP_RES_MAP m
        where EAM_RES_GRP_RES_MAP.resource_group_id = m.resource_group_id
        and EAM_RES_GRP_RES_MAP.resource_id > m.resource_id
    ) from dual
)

